sp_widget.script:
(function() {
    //Query active book issue to the logged-in user
    var grBookIssueDetail = new GlideRecord('x_089_lms_book_issue_detail'); //name of the book issue table
    grBookIssueDetail.addQuery('active', true);
    grBookIssueDetail.addQuery('assigned_to', gs.getUserID());
    grBookIssueDetail.orderBy('sys_created_on');
    grBookIssueDetail.query();

    data.bookIssues = []; //initialise an array in object data
    while(grBookIssueDetail.next()) {
        data.bookIssues.push({
            number: grBookIssueDetail.number.toString(),
            book: grBookIssueDetail.book.title.toString()
        });
    }
})();
